sys_script_include.script:
var GetUsersDepartment = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetUsersDepartment.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define the getDepartment function.  
	// Create a GlideRecord for the User table.
	// Create a GlideRecord for the Department table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Use the department reference ID from the User table.
	// Return the department Name for the requested record
	getDepartment: function() {
		var userRecord = new GlideRecord("sys_user");
		var departmentRecord = new GlideRecord("cmn_department");
		userRecord.get(this.getParameter('sysparm_userID'));
		departmentRecord.get(userRecord.department);

		return departmentRecord.name + '';
	},
	type: 'GetUsersDepartment'
});
